$('.open-content').click(function() {
    var n;
    var tgt;

    tgt = $(this).attr('target');
    n   = parseInt(tgt);
    $('.content-hidden').removeClass('is-active');
    $(this).toggleClass('is-active');
    $('.content-hidden-' + n).toggleClass('is-active');
});